import React, { Component } from 'react';
import axios from 'axios';
import MainVideo from './MainVideo'
import MainVideoDetails from './MainVideoDetails'
import AddComment from './AddComment'
import Comment from './Comment'
// import { BrowserRouter as Router, Link } from "react-router-dom";
import CommentsList from './CommentsList'
import NextVideo from './NextVideo'
import viewIcon from '../assets/Icons/SVG/Icon-views.svg';
import likeIcon from '../assets/Icons/SVG/Icon-likes.svg';

class MainContent extends Component {

    state = {
        // listComments: [],
        videoDetails: []
    };

    componentDidMount() {

        // getting data with Axios with arrow function
        let id = "1af0jruup5gu";
        axios.get("https://project-2-api.herokuapp.com/videos/" + id + "?api_key=9a9fc026-4b85-43b1-a00a-ecf8587e838f")
            .then(response => {
                this.setState({
                    videoDetails: response.data,
                    listComments: response.data.comments
                });
                console.log("video --> ", this.state.videoDetails);
                console.log("comments --> ", this.state.listComments)

            });

        // // getting data with Axios with arrow function
        // axios.get("https://project-2-api.herokuapp.com/videos/1a3cjruucpf7?api_key=9a9fc026-4b85-43b1-a00a-ecf8587e838f")
        //     .then(response => {
        //         this.setState({
        //             arrayComments: response.data,
        //             listComments: response.data.comments
        //         });
        //         console.log(this.state.listComments)
        //     });
    }

    render() {
        const { id, title, channel, image, description, views, likes, video, timestamp, comments } = this.state.videoDetails;
        if (this.state.listComments.lenght === 0 || this.state.arrayComments === "") {
            return <h1>LOADING</h1>
            // console.log(<h2>ERROR</h2>);
        } else

            return (
                <>
                    <MainVideo />
                    <main className="main-structure">
                        <section className="main-structure__left">
                            {/* <h1>title</h1>{this.state.videoDetails.title} */}
                            {/* <MainVideoDetails /> */}

                            <div className="video__details">
                                <h1 className="video__details__title">{title}</h1>
                                <div className="video__details-top">
                                    <div className="video__details-author-date">
                                        <h3>{channel}</h3>
                                        <h4 className="video__details-author-date__date">{timestamp}</h4>
                                    </div>
                                    <div className="video__details__views-likes">
                                        <img className="video__details__icon" src={viewIcon} alt="Views" />
                                        <h4 className="video__details__nums">{views}</h4>
                                        <img className="video__details__icon" src={likeIcon} alt="Likes" />
                                        <h4 className="video__details__nums video__details__nums-right">{likes}</h4>
                                    </div>
                                </div>
                                <hr className="video__divider"></hr>
                                <span>
                                    <h4 className="video__details__description">
                                        {description}
                                    </h4>
                                </span>
                                <h3 className="video__comments-total">3 Comments</h3>
                            </div>

                            <AddComment />

                            <div className="comments-list">
                                {
                                    // this.state.videoDetails.comments.map(comment => {
                                    //     return <Comment key={comment.id} id={comment.id} name={comment.name} comment={comment.comment} timestamp={comment.timestamp} />
                                    // })
                                }
                            </div>
                        </section>
                        <aside className="main-structure__aside">
                            <NextVideo videos={this.props.videos} />
                        </aside>
                    </main>
                </>
            );
    };
};


export default MainContent;
