import React from 'react';
import userPicture from '../assets/Images/Mohan-muruge.jpg';

// data
const arrayComments = [
    {
        name: "Gary Wong",
        timestamp: "12/18/2018",
        comment: "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!"
    },
    {
        name: "Theodore Duncan",
        timestamp: "11/15/2018",
        comment: "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!"
    },
    {
        name: "Micheal Lyons",
        timestamp: "12/12/2018",
        comment: "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed."
    }
];

function CommentsList(props) {
    console.log(props.secondProp);
    const commList = arrayComments.map(arrayComments => {
        return (
            <>
                <div className="comments-list__item">
                    <div className="comments-list__left">
                        <img className="comments-list__user-picture" src={userPicture} alt="" />
                    </div>
                    <div className="comments-list__right">
                        <div className="comments-list__right-top">
                            <span>{arrayComments.name}</span>
                            <span className="comments-list__date">{arrayComments.timestamp}</span>
                        </div>
                        <span>{arrayComments.comment}</span>
                    </div>
                </div>
                <hr className="comments-list__divider"></hr>
            </>
        );

    })
    return (
        <div className="comments-list">
            {commList}
        </div>
    );
}

export default CommentsList;
