import React from 'react';
// import userPicture from '../assets/Images/Mohan-muruge.jpg';

// data
const arrayComments = [
    {
        name: "Gary Wong",
        timestamp: "12/18/2018",
        comment:
            "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!"
    },
    {
        name: "Theodore Duncan",
        timestamp: "11/15/2018",
        comment:
            "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!"
    },
    {
        name: "Micheal Lyons",
        timestamp: "12/12/2018",
        comment:
            "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed."
    }
];


// function List(props) {
//     const studentList = students.map(student => {
//         return <li>{student}</li>
//     })
//     return (
//         <ul className="student-list">
//             {studentList}
//         </ul>
//     );
// }

function CommentsList(props) {
    const commList = arrayComments.map(arrayComments => {
        return (
            <>
                <div>
                    <span>{arrayComments}</span>
                    <span>date</span>
                </div>
                <span>comment</span>
            </>
        )

    })
    return (
        <div className="comments-list">
            <div className="comments__list__left">
                <img className="comments__list__user-picture" src={userPicture} alt="" />
            </div>
            <div className="comments__list__right">
                {commList}
            </div>
            <hr className="comments__divider"></hr>
        </div>
    );
}

export default CommentsList;
