import React, { Component } from 'react';
import Video from './Video';


class NextVideo extends Component {
    render() {
        let arrVideos = this.props.videos;
        let activeVideo = this.props.idVideo;

        for (let i = 0; i < arrVideos.length; i++) {
            if (arrVideos.id[i] === activeVideo) {
                let newArr = arrVideos.splice(i, 1);
            }
        }

        console.log("arrVideos: ", arrVideos);
        console.log("sem ativo: ", newArr);
        return (
            <div className="next-video">
                <h5 className="next-video__heading">NEXT VIDEO</h5>
                {
                    this.props.videos.map(video => {
                        return <Video key={video.id} id={video.id} title={video.title} channel={video.channel} image={video.image} />
                    })
                }
            </div>

        );
    }
}

export default NextVideo;
